app.controller('taskController', function ($scope, taskResource, clientResource, $uibModal, $location, $timeout, statusResource, moduleResource, urgencyResource, UserClientResource, $rootScope, usersResource) {

  $scope.vm = {
    done : '',
    client : ''
  };

  // obtiene las tareas
  function getTasks() {
    //$scope.tasks = taskResource.query({ done: 'False'});
    $scope.tasks = UserClientResource.query({ user_id : $rootScope.userId, task_done: 'False'});
  }
  getTasks();

  // marca como realizada la tarea
  $scope.doneTask = function(taskId) {
    taskResource.get({ taskId:taskId }, function(task) {
      task.done = true;
      taskResource.update({ taskId:task.id }, task);
    });
    getTasks();
  };

  // borra la tarea
  $scope.deleteTask = function(taskId) {
    taskResource.delete({ id : taskId });
    $timeout(getTasks, 500);
  };

  // obtiene todos los clientes
  getClients = function() {
    UserClientResource.query({user_id : $rootScope.userId }, function(data){
      all = {
        id : '',
        name : 'Todos',
        lastname : '',
        organization : 0
      }
      data.unshift(all);
      $scope.clients = data;
    });
  };
  getClients();

  // carga select con clientes
  usersResource.query({}, function(data){
    $scope.clientsM = data;
  });

  // carga select con estados
  statusResource.query({}, function(data){
    $scope.Tstatus = data;
  });

  // carga select con modulos
  moduleResource.query({}, function(data){
    $scope.modules = data;
  });
  
  urgencyResource.query({}, function(data){
    $scope.urgency = data;
  });

  // crea tarea
  $scope.newTask = function() {
    $scope.task.user = $rootScope.userId;
    var uc = {}
    var nTask = $scope.task
    taskResource.save(nTask, function(tr){
      // carga relacion usuario cliente tarea
      uc.task = tr.id
      uc.user = tr.user
      uc.client = tr.client
      UserClientResource.save(uc);
    });

    $('#myModal').modal('hide');
    $timeout(getTasks, 500);
  };
})

